import { MachineConfig, send, Action, assign } from "xstate";

import { inspect } from '@xstate/inspect';

inspect({
  url: "https://statecharts.io/inspect",
  iframe: false
});

const getDataFromAPI = (userId) =>
    fetch(`https://nlu-heroku.herokuapp.com/`).then(data => data.json());

function say(text: string): Action<SDSContext, SDSEvent> {
    return send((_context: SDSContext) => ({ type: "SPEAK", value: text }))
}

function listen(): Action<SDSContext, SDSEvent> {
    return send('LISTEN')
}

const grammar: { [index: string]: { person?: string, day?: string, time?: string, agree?: string, refuse?: string, appointment?: string, todoitem?: string, timer?: string } } = {
    "John": { person: "John Appleseed" },
    "Mary": { person: "Mary Orangeseed" },
    "Dan": { person: "Dan Cherryseed" },
    "Steve": { person: "Steve Strawberryseed" },
    "Klaus": { person: "Klaus Pearseed" },
    "Gus": { person: "Gus Bananaseed" },
    "Jennifer": { person: "Jennifer Pineappleseed" },
    "Monday": { day: "Monday" },
    "Tuesday": { day: "Tuesday" },
    "Wednesday": { day: "Wednesday" },
    "Thursday": { day: "Thursday" },
    "Friday": { day: "Friday" },
    "Saturday": { day: "Saturday" },
    "Sunday": { day: "Sunday" },
    "next Monday": { day: "Monday" },
    "next Tuesday": { day: "Tuesday" },
    "next Wednesday": { day: "Wednesday" },
    "next Thursday": { day: "Thursday" },
    "next Friday": { day: "Friday" },
    "next Saturday": { day: "Saturday" },
    "next Sunday": { day: "Sunday" },
    "on Monday": { day: "Monday" },
    "on Tuesday": { day: "Tuesday" },
    "on Wednesday": { day: "Wednesday" },
    "on Thursday": { day: "Thursday" },
    "on Friday": { day: "Friday" },
    "on Saturday": { day: "Saturday" },
    "on Sunday": { day: "Sunday" },
    "it's at 8": { time: "08:00" },
    "it's at 9": { time: "09:00" },
    "it's at 10": { time: "10:00" },
    "it's at 11": { time: "11:00" },
    "it's at 12": { time: "12:00" },
    "it's at 1": { time: "13:00" },
    "it's at 2": { time: "14:00" },
    "it's at 3": { time: "15:00" },
    "it's at 4": { time: "16:00" },
    "it's at 5": { time: "17:00" },
    "it's at 6": { time: "18:00" },
    "at 8": { time: "08:00" },
    "at 9": { time: "09:00" },
    "at 10": { time: "10:00" },
    "at 11": { time: "11:00" },
    "at 12": { time: "12:00" },
    "at 1": { time: "13:00" },
    "at 2": { time: "14:00" },
    "at 3": { time: "15:00" },
    "at 4": { time: "16:00" },
    "at 5": { time: "17:00" },
    "at 6": { time: "18:00" },
    "8": { time: "08:00" },
    "9": { time: "09:00" },
    "10": { time: "10:00" },
    "11": { time: "11:00" },
    "12": { time: "12:00" },
    "1": { time: "13:00" },
    "2": { time: "14:00" },
    "3": { time: "15:00" },
    "4": { time: "16:00" },
    "5": { time: "17:00" },
    "6": { time: "18:00" },
    "around 8": { time: "08:00" },
    "around 9": { time: "09:00" },
    "around 10": { time: "10:00" },
    "around 11": { time: "11:00" },
    "around 12": { time: "12:00" },
    "around 1": { time: "13:00" },
    "around 2": { time: "14:00" },
    "around 3": { time: "15:00" },
    "around 4": { time: "16:00" },
    "around 5": { time: "17:00" },
    "around 6": { time: "18:00" },
    "yes": { agree: "yes" },
    "yeah": { agree: "yes" },
    "sounds good": { agree: "yes" },
    "of course": { agree: "yes" },
    "probably": { agree: "yes" },
    "it's fine": { agree: "yes" },
    "no": { refuse: "no" },
    "I'm not sure": { refuse: "no" },
    "I don't think so": { refuse: "no" },
    "no way": { refuse: "no" },
    "absolutely not": { refuse: "no" },
    "I want to book an appointment": { appointment: "appointment" },
    "rendez-vous": { appointment: "appointment" },
    "appointment": { appointment: "appointment" },
    "Let's create an appointment": { appointment: "appointment" },
    "I want an appointment": { appointment: "appointment" },
    "appointment": { appointment: "appointment" },
    "schedule": { appointment: "appointment" },
    "to-do list": { todoitem: "todoitem" },
    "a to-do list": { todoitem: "todoitem" },
    "list": { todoitem: "todoitem" },
    "Let's start a to-do list": { todoitem: "todoitem" },
    "I have something to do": { todoitem: "todoitem" },
    "to do list": { todoitem: "todoitem" },
    "timer": { timer: "timer" },
    "a timer": { timer: "timer" },
    "start the timer": { timer: "timer" },
    "ring": { timer: "timer" },
    "minute": { timer: "timer" },
}


export const dmMachine: MachineConfig<SDSContext, any, SDSEvent> = ({
    initial: 'init',
    states: {
        init: {
            on: {
                CLICK: 'query'
            }
        },
        query: {
            invoke: {
                id: 'getUser',
                initial: 'idle',
                src: (context, event) => getDataFromAPI(context.userId),
                onDone: {
                    target: 'choice',
                    //actions: (context, event) => console.log(event.data)
                    actions: assign({ results: (context:SDSContext, event:any) => event.data })
                },
                onError: {
                    target: 'failure',
                    //actions: (context, event) => console.log(event.data)
                    actions: assign({ errorMessage: (context:SDSContext, event:any) => event.data})
                }
            }
        },
        failure: {
            initial: "prompt",
            on: { ENDSPEECH: "init" },
            states: {
                prompt: { entry: say("There is an error, please retry later.") }
            }
        },
        choice: {
            initial: "prompt",
            on: {
                RECOGNISED: [{
                    cond: (context) => "appointment" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { appointment: grammar[ context.recResult].appointment } }),
                    target: "welcome"
                },
                {
                    cond: (context) => "todoitem" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { todoitem: grammar[ context.recResult].todoitem } }),
                    target: "welcomeTodo"
                },
                {
                    cond: (context) => "timer" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { timer: grammar[ context.recResult].timer } }),
                    target: "welcomeTimer"
                },
                { target: ".nomatch" }]
            },
            states: {
                prompt: {
                    entry: say("What would you like to do?"),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry, what do you mean?"),
                    on: { ENDSPEECH: "prompt" }
                }
            }
        },
        welcome: {
            initial: "prompt",
            on: { ENDSPEECH: "who" },
            states: {
                prompt: { entry: say("Let's create an appointment!") }
            }
        },
        welcomeTodo: {
            initial: "prompt",
            on: { ENDSPEECH: "init" },
            states: {
                prompt: { entry: say("Let's create a to-do list!") }
            }
        },
        welcomeTimer: {
            initial: "prompt",
            on: { ENDSPEECH: "init" },
            states: {
                prompt: { entry: say("Let's start a timer!") }
            }
        },
        who: {
            initial: "prompt",
            on: {
                RECOGNISED: [{
                    cond: (context) => "person" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { person: grammar[context.recResult].person } }),
                    target: "day"

                },
                { target: ".nomatch" }]
            },
            states: {
                prompt: {
                    entry: say("Who are you meeting with?"),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry I don't know them"),
                    on: { ENDSPEECH: "prompt" }
                }
            }
        },
        day: {
            initial: "prompt",
            on: {
                RECOGNISED: [{
                    cond: (context) => "day" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { day: grammar[context.recResult].day } }),
                    target: "duration"

                },
                { target: ".nomatch" }]
            },
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: 'On which day is your meeting?',
                    })),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry ..."),
                    on: { ENDSPEECH: "prompt" }
                },
            }
        },
        duration: {
            initial: "prompt",
            on: {
                RECOGNISED: [{
                    cond: (context) => "agree" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { agree: grammar[context.recResult].agree } }),
                    target: "confirmationWholeDay"
                },
                {   
                    cond: (context) => "refuse" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { refuse: grammar[context.recResult].refuse } }),
                    target: "time"
                },
                { target: ".nomatch" }],
            },
            states: {
                prompt: {
                    entry: say("Will it take the whole day?"),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry ..."),
                    on: { ENDSPEECH: "prompt" }
                },
            }
        },
        time: {
            initial: "prompt",
            on: {
                RECOGNISED: [{
                    cond: (context) => "time" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { time: grammar[context.recResult].time } }),
                    target: "regularConfirmation"
                },
                { target: ".nomatch" }]
            },
            states: {
                prompt: {
                    entry: say("What time is your meeting?"),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry ..."),
                    on: { ENDSPEECH: "prompt" }
                },
            }
        },
        regularConfirmation: {
            initial: "prompt",
            on: {
                RECOGNISED: [{
                    cond: (context) => "agree" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { agree: grammar[context.recResult].agree } }),
                    target: "confirmation"
                },
                {   
                    cond: (context) => "refuse" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { refuse: grammar[context.recResult].refuse } }),
                    target: "who"
                },
                { target: ".nomatch" }],
            },
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `Do you want me to create an appointment with ${context.person} on ${context.day} at ${context.time} ?`
                    })),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry ..."),
                    on: { ENDSPEECH: "prompt" }
                },
            }
        },
        confirmationWholeDay: {
            initial: "prompt",
            on: {
                RECOGNISED: [{
                    cond: (context) => "agree" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { agree: grammar[context.recResult].agree } }),
                    target: "confirmation"
                },
                {   
                    cond: (context) => "refuse" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { refuse: grammar[context.recResult].refuse } }),
                    target: "who"
                },
                { target: ".nomatch" }],
            },
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: `Do you want me to create an appointment with ${context.person} on ${context.day} for the whole day?`
                    })),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry ..."),
                    on: { ENDSPEECH: "prompt" }
                },
            }
        },
        confirmation: {
            initial: "prompt",
            on: { ENDSPEECH: "init" },
            states: {
                prompt: { entry: say("Your appointment has been created!") }
            }
        }
    }
})
