import { MachineConfig, send, Action, assign } from "xstate";

import { inspect } from '@xstate/inspect';

inspect({
  url: "https://statecharts.io/inspect",
  iframe: false
});

function say(text: string): Action<SDSContext, SDSEvent> {
    return send((_context: SDSContext) => ({ type: "SPEAK", value: text }))
}

function listen(): Action<SDSContext, SDSEvent> {
    return send('LISTEN')
}

const grammar: { [index: string]: { person?: string, day?: string, time?: string, agree?: string, refuse?: string } } = {
    "John": { person: "John Appleseed" },
    "meeting": { person: "Meeting" },
    "Mary": { person: "Mary Orangeseed" },
    "Dan": { person: "Dan Cherryseed" },
    "Steve": { person: "Steve Strawberryseed" },
    "Klaus": { person: "Klaus Pearseed" },
    "Gus": { person: "Gus Bananaseed" },
    "Jennifer": { person: "Jennifer Pineappleseed" },
    "on Monday": { day: "Monday" },
    "on Tuesday": { day: "Tuesday" },
    "on Wednesday": { day: "Wednesday" },
    "on Thursday": { day: "Thursday" },
    "on Friday": { day: "Friday" },
    "on Saturday": { day: "Saturday" },
    "on Sunday": { day: "Sunday" },
    "at eight": { time: "08:00" },
    "at nine": { time: "09:00" },
    "at ten": { time: "10:00" },
    "at eleven": { time: "11:00" },
    "at noon": { time: "12:00" },
    "at one": { time: "13:00" },
    "at two": { time: "14:00" },
    "at three": { time: "15:00" },
    "at four": { time: "16:00" },
    "at five": { time: "17:00" },
    "at six": { time: "18:00" },
    "at seven": { time: "19:00" },
    "yes": { agree: "yes" },
    "no": { refuse: "no" },
    "of course": { agree: "yes" },
    "no way": { refuse: "no" },
}


export const dmMachine: MachineConfig<SDSContext, any, SDSEvent> = ({
    initial: 'init',
    states: {
        init: {
            on: {
                CLICK: 'welcome'
            }
        },
        welcome: {
            initial: "prompt",
            on: { ENDSPEECH: "who" },
            states: {
                prompt: { entry: say("Let's create an appointment") }
            }
        },
        who: {
            initial: "prompt",
            on: {
                RECOGNISED: [{
                    cond: (context) => "person" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { person: grammar[context.recResult].person } }),
                    target: "day"

                },
                { target: ".nomatch" }]
            },
            states: {
                prompt: {
                    entry: say("Who are you meeting with?"),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry I don't know them"),
                    on: { ENDSPEECH: "prompt" }
                }
            }
        },
        day: {
            initial: "prompt",
            on: {
                RECOGNISED: [{
                    cond: (context) => "day" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { day: grammar[context.recResult].day } }),
                    target: "duration"

                },
                { target: ".nomatch" }]
            },
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: 'Ok. ${context.person}. On which day is your meeting?'
                    })),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry I don't understand what you mean"),
                    on: { ENDSPEECH: "prompt" }
                },
            }
        },
        duration: {
            initial: "prompt",
            on: {
                RECOGNISED: [{
                    cond: (context) => "agree" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { agree: grammar[context.recResult].agree } }),
                    target: "confirmationWholeDay"
                },
                {   
                    cond: (context) => "refuse" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { refuse: grammar[context.recResult].refuse } }),
                    target: "time"
                },
                { target: ".nomatch" }],
            },
            states: {
                prompt: {
                    entry: say("Will it take the whole day?"),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry I don't understand what you mean"),
                    on: { ENDSPEECH: "prompt" }
                },
            }
        },
        time: {
            initial: "prompt",
            on: {
                RECOGNISED: [{
                    cond: (context) => "time" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { time: grammar[context.recResult].time } }),
                    target: "regularConfirmation"
                },
                { target: ".nomatch" }]
            },
            states: {
                prompt: {
                    entry: say("What time is your meeting?"),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry I don't understand what you mean"),
                    on: { ENDSPEECH: "prompt" }
                },
            }
        },
        regularConfirmation: {
            initial: "prompt",
            on: {
                RECOGNISED: [{
                    cond: (context) => "agree" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { agree: grammar[context.recResult].agree } }),
                    target: "confirmation"
                },
                {   
                    cond: (context) => "refuse" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { refuse: grammar[context.recResult].refuse } }),
                    target: "who"
                },
                { target: ".nomatch" }],
            },
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: 'Do you want me to create an appointment with ${context.person} on ${context.date} at ${context.time} ?'
                    })),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry I don't understand what you mean"),
                    on: { ENDSPEECH: "prompt" }
                },
            }
        },
        confirmationWholeDay: {
            initial: "prompt",
            on: {
                RECOGNISED: [{
                    cond: (context) => "agree" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { agree: grammar[context.recResult].agree } }),
                    target: "confirmation"
                },
                {   
                    cond: (context) => "refuse" in (grammar[context.recResult] || {}),
                    actions: assign((context) => { return { refuse: grammar[context.recResult].refuse } }),
                    target: "who"
                },
                { target: ".nomatch" }],
            },
            states: {
                prompt: {
                    entry: send((context) => ({
                        type: "SPEAK",
                        value: 'Do you want me to create an appointment with ${context.person} on ${context.date} for the whole day?'
                    })),
                    on: { ENDSPEECH: "ask" }
                },
                ask: {
                    entry: listen()
                },
                nomatch: {
                    entry: say("Sorry I don't understand what you mean"),
                    on: { ENDSPEECH: "prompt" }
                },
            }
        },
        confirmation: {
            initial: "prompt",
            on: { ENDSPEECH: "init" },
            states: {
                prompt: { entry: say("Your appointment has been created!") }
            }
        }
    }
})
