//
//  quotesgrammar.js
//
/*
to do is to be	Socrates
to be is to do	Sartre
do be do be do	Sinatra
*/
gram = new Grammar('funnyquotes');

gram.funnyquotes = [Ref("quote"), Tag("out.type=rules.quote;")];
gram.quote = [OneOf([
              [["to","do","is","to","be"], Tag("out='Socrates'")],
			  [["to","be","is","to","do"], Tag("out='Sartre'")],
			  [["do","be","do","be","do"], Tag("out='coke'")]])];
